import { documentToHtmlString } from '@contentful/rich-text-html-renderer';

export default function Recipe({recipe}){
    const title = recipe.title
    const description = recipe.description
    const ingredients = recipe.ingredients
    const instructions = recipe.ingredients

    const descriptionRT = recipe.descriptionRt
    documentToHtmlString(recipe.ingredientsRt).then( renderedHtml => {
        document.getElementById('rich-text-body').innerHTML = renderedHtml;
    })
    const instructionsRT = recipe.ingredientsRt

    console.log('RECIPE: ', recipe);
    console.log('Titel: ', title);
    console.log('Description: ', description);
    console.log('ingredients: ', ingredients);
    console.log('instructions: ', instructions);

    console.log('DescriptionRT: ', descriptionRT);
    console.log('ingredientsRT: ', ingredientsRT);
    console.log('instructionsRT: ', instructionsRT);

    return (
        <div>
            <h1>{title}</h1>
            <h2>{description}</h2>
            <div>
                <h3>Ingredients</h3>
                <p>{ingredients}</p>
                <div id="rich-text-body"></div>
            </div>
            <div>
            <h3> How to</h3>
            <p>
                {instructions}
            </p>
            </div>
        </div>
    )
}
