import { documentToReactComponents } from '@contentful/rich-text-react-renderer';
import { getEntry } from '../lib/api'
import NutrientTable from '../components/NutrientTable'
export default function Recipe(props){
    const title = props.recipe.title

    const description = documentToReactComponents(props.recipe.descriptionRt) 
    const ingredientsDisplay = documentToReactComponents(props.recipe.ingredientsRt)
    const instructionsDisplay = documentToReactComponents(props.recipe.instructionsRt)     


    getEnergyNutrients(props.recipe.nutrients)
    function getEnergyNutrients(nutrients) {
        const calories = nutrients['Energi (kcal)']
        const protein = nutrients['Protein']
        const carbohodydrates = nutrients['Kolhydrater']
        const fat = nutrients['Fett']
        console.log(' Energi: ');
        console.log(protein);
        console.log(carbohodydrates);
        console.log(fat);
        console.log(calories);
    }

    return (
        <div id={title}>
            <h1>{title}</h1>
            <h2>{description}</h2>
            <div>
                <h3>Ingredients</h3>
                {ingredientsDisplay}
            </div>
            <div>
                <h3> How to</h3>
                <p>
                    {instructionsDisplay}
                </p>
                <NutrientTable recipeTitle={title} nutrients={props.recipe.nutrients} />
            </div>
        </div>
    )
}
